import React, { useState, useEffect } from "react";

// Sample blog data
const blogs = [
  {
    id: 1,
    title: "The High Cost of Victory: Olympic Economics",
    date: "Apr 15, 2024",
    tags: ["Olympic"],
    
    img: `data:image/jpeg;base64,`,
    content: `The Olympics are more than just a sports competition; they're a massive global spectacle...\n    ...Ultimately, the Olympics are a complex mix of sports, politics, economics, and culture.\n    \n              Beyond finances, the Olympics are a powerful platform for\n              countries to show off their culture and achievements to the world.\n              It's a chance to build bridges, promote peace, and inspire people.\n              The Games can also leave a lasting positive impact on a city, with\n              improved facilities and a boost to tourism. It is also a\n              demonstration of a country's prowess to host an event on the\n              grandest of scales.\n            \n            \n\n            \n              The Paris Olympics 2024 embrace the concept of “doing more with\n              less” and are the first games to fully align with the Olympic\n              Agenda 2020, which is the strategic roadmap to make the Olympics\n              more sustainable, cost-effective, and aligned with the needs of\n              host cities and communities.\n            \n\n          \n              Imagine the excitement if India hosted the Olympics! It would be a\n              huge opportunity to showcase our country's rich culture and\n              diversity to the world.\n            \n\n            \n              The games aren’t so different from the startup world which we are\n              all familiar with. Both involve some of the most dedicated people\n              who persevere and compete to be successful. Ultimately, the\n              Olympics are a complex mix of sports, politics, economics, and\n              culture. While the focus is on the athletes, the Games have a\n              far-reaching impact on the world.`          
  },
  {
    id: 2,
    title: "Sustainable Cities and the Future",
    date: "May 10, 2024",
    tags: ["Sustainability", "Urban"],
    
    img:` data:image/jpeg;base64,`,
    content: `Sustainability in urban areas is becoming increasingly important...\n    ...Cities that embrace green technology see long-term benefits.`
  },
  {
    id: 3,
    title: "Tech Startups and Innovation",
    date: "Jun 5, 2024",
    tags: ["Tech", "Startup"],
    
    img: `data:image/webp;base64,`,
    content: `Startups are the backbone of innovation...\n    ...The entrepreneurial spirit drives progress in all sectors.`
  }
];

export default function BlogPage() {
  const [selectedBlogId, setSelectedBlogId] = useState(null);
  // Add state for blog ID input and filtered blog
  const [blogIdInput, setBlogIdInput] = useState('');
  const [filteredBlog, setFilteredBlog] = useState(null);

  const selectedBlog = blogs.find(blog => blog.id === selectedBlogId);

  // Handle input change to filter blogs
  const handleInputChange = (e) => {
    const id = e.target.value;
    setBlogIdInput(id);
    if (id) {
      const foundBlog = blogs.find(blog => blog.id === parseInt(id));
      setFilteredBlog(foundBlog);
    } else {
      setFilteredBlog(null);
    }
  };

  // Handle initial load for hash-based routing
  useEffect(() => {
    if (window.location.hash) {
      const hashId = parseInt(window.location.hash.replace('#blog-', ''));
      if (!isNaN(hashId)) {
        setSelectedBlogId(hashId);
      }
    }
  }, []);

  return (
    <div className="min-h-screen bg-[#0a0a2a] text-white px-6 md:px-20 py-10">
      {/* Breadcrumb */}
      <div className="text-sm text-gray-400 mb-4">
        Home / Blog / {selectedBlog || filteredBlog ? (selectedBlog || filteredBlog).title : "All Blogs"}
      </div>

      {/* Blog ID Input */}
      <div className="mb-6">
        <input
          type="number"
          placeholder="Enter Blog ID (e.g., 1)"
          className="w-full md:w-1/3 p-2 rounded-md bg-[#1a1a3a] border border-gray-700 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={blogIdInput}
          onChange={handleInputChange}
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
        {/* Main Blog Content */}
        <div className="lg:col-span-3">
          {filteredBlog ? (
            <div className="bg-[#1a1a3a] p-6 rounded-lg shadow-lg">
              <div className="rounded-lg overflow-hidden mb-6">
                <img
                  src={filteredBlog.img || "/placeholder.svg"}
                  alt={filteredBlog.title}
                  className="w-full rounded-lg"
                />
                <p className="text-xs text-gray-400 mt-1">src: NBC News</p>
              </div>

              <h1 className="text-3xl md:text-4xl font-bold mb-4">
                {filteredBlog.title}
              </h1>

              <p className="text-gray-400 mb-2 text-sm">
                {filteredBlog.date} • {filteredBlog.tags.join(", ")}
              </p>

              <p className="text-gray-200 leading-relaxed">{filteredBlog.content}</p>

              <button
                className="mt-6 px-4 py-2 bg-blue-600 rounded hover:bg-blue-700"
                onClick={() => { setFilteredBlog(null); setBlogIdInput(''); setSelectedBlogId(null); window.location.hash = ''; }}
              >
                ← Back to All Blogs
              </button>
            </div>
          ) : selectedBlog ? (
            <div className="bg-[#1a1a3a] p-6 rounded-lg shadow-lg">
              <div className="rounded-lg overflow-hidden mb-6">
                <img
                  src={selectedBlog.img || "/placeholder.svg"}
                  alt={selectedBlog.title}
                  className="w-full rounded-lg"
                />
                <p className="text-xs text-gray-400 mt-1">src: NBC News</p>
              </div>

              <h1 className="text-3xl md:text-4xl font-bold mb-4">
                {selectedBlog.title}
              </h1>

              <p className="text-gray-400 mb-2 text-sm">
                {selectedBlog.date} • {selectedBlog.tags.join(", ")}
              </p>

              <p className="text-gray-200 leading-relaxed">{selectedBlog.content}</p>

              <button
                className="mt-6 px-4 py-2 bg-blue-600 rounded hover:bg-blue-700"
                onClick={() => { setSelectedBlogId(null); window.location.hash = ''; }}
              >
                ← Back to All Blogs
              </button>
            </div>
          ) : (
            <div className="grid gap-6">
              {blogs.map(blog => (
                <div
                  key={blog.id}
                  className="bg-[#1a1a3a] p-4 rounded-lg shadow-md cursor-pointer hover:shadow-xl transition-shadow duration-300"
                  onClick={() => { setSelectedBlogId(blog.id); window.location.hash = `blog-${blog.id}`; }}
                >
                  <div className="rounded-lg overflow-hidden mb-3">
                    <img
                      src={blog.img || "/placeholder.svg"}
                      alt={blog.title}
                      className="w-full h-48 object-cover rounded-lg"
                    />
                  </div>
                  <h2 className="text-xl font-semibold mb-1">{blog.title}</h2>
                  <p className="text-gray-400 text-sm mb-2">{blog.tags.join(", ")}</p>
                  <p className="text-gray-300 text-sm line-clamp-3">{blog.content}</p>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Sidebar */}
        <div className="lg:col-span-1">
          <div className="bg-[#10103a] p-4 rounded-lg shadow-md space-y-4">
            <h3 className="text-lg font-semibold mb-3">All Blogs</h3>
            {blogs.map(blog => (
              <div
                key={blog.id}
                className="flex items-center gap-3 cursor-pointer hover:bg-[#202050] p-2 rounded transition-colors"
                onClick={() => { setSelectedBlogId(blog.id); window.location.hash = `blog-${blog.id}`; }}
              >
                <img
                  src={blog.img || "/placeholder.svg"}
                  alt={blog.title}
                  className="w-16 h-16 rounded-lg object-cover"
                />
                <div>
                  <h4 className="text-sm font-semibold">{blog.title}</h4>
                  <p className="text-xs text-gray-400">{blog.tags.join(", ")}</p>
                  <p className="text-xs text-gray-400">{blog.date}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
